package com.thingtrack.parse;import java.util.ArrayList;import java.util.Date;import org.tacografo.file.cardblockdriver.subblock.CardActivityDailyRecord;import org.tacografo.file.vublock.subblock.VuCardIWRecord;import com.fasterxml.jackson.annotation.JsonFormat;public class Activity {	@JsonFormat(shape=JsonFormat.Shape.STRING, pattern="yyyy-MM-dd'T'HH:mm:ss.SSS'Z'",timezone="GMT")	private Date date;	private ArrayList<ActivityChangeInfo> activityChangeInfo;	private String organizationId;	private String cardNumber;	private String registration;	private int distance;	private ArrayList<String> files;	private ArrayList<VehicleChangeInfo> vehicles;	private ArrayList<Places> places;	private ArrayList<SpeedVehicle> speed;		public Activity(){		this.activityChangeInfo=new ArrayList<ActivityChangeInfo>();		this.places=new ArrayList<Places>();		this.files=new ArrayList<String>();		this.vehicles=new ArrayList<VehicleChangeInfo>();	}	public Activity(VuCardIWRecord vc,Date d) {		this.activityChangeInfo=new ArrayList();		this.files=new ArrayList();		this.vehicles=new ArrayList();		this.places=new ArrayList();		this.speed= new ArrayList();		this.activityChangeInfo=vc.getActvityChangeInfo();		this.date=d;	}	public Activity(CardActivityDailyRecord acadr){		this.activityChangeInfo=new ArrayList();		this.files=new ArrayList();		this.vehicles=new ArrayList();		this.places=new ArrayList();		this.speed= new ArrayList();		this.activityChangeInfo=acadr.getComActivityChangeInfo();		this.date=acadr.getActivityRecordDate();	}			/**	 * @return the cardNumber	 */	public String getCardNumber() {		return cardNumber;	}	/**	 * @param cardNumber the cardNumber to set	 */	public void setCardNumber(String cardNumber) {		this.cardNumber = cardNumber;	}	/**	 * @return the registration	 */	public String getRegistration() {		return registration;	}	/**	 * @param registration the registration to set	 */	public void setRegistration(String registration) {		this.registration = registration;	}	/**	 * @return the date	 */	public Date getDate() {		return date;	}	/**	 * @param date the date to set	 */	public void setDate(Date date) {		this.date = date;	}	/**	 * @return the activityChangeInfo	 */	public ArrayList<ActivityChangeInfo> getActivityChangeInfo() {		return activityChangeInfo;	}	/**	 * @param activityChangeInfo the activityChangeInfo to set	 */	public void setActivityChangeInfo(ArrayList<ActivityChangeInfo> activityChangeInfo) {		this.activityChangeInfo = activityChangeInfo;	}	/**	 * @return the organizationId	 */	public String getOrganizationId() {		return organizationId;	}	/**	 * @param organizationId the organizationId to set	 */	public void setOrganizationId(String organizationId) {		this.organizationId = organizationId;	}	/**	 * @return the distance	 */	public int getDistance() {		return distance;	}	/**	 * @param distance the distance to set	 */	public void setDistance(int distance) {		this.distance = distance;	}	/**	 * @return the files	 */	public ArrayList<String> getFiles() {		return files;	}	/**	 * @param files the files to set	 */	public void setFiles(ArrayList<String> files) {		this.files = files;	}	/**	 * @return the vehicle	 */	public ArrayList<VehicleChangeInfo> getVehicles() {		return vehicles;	}	/**	 * @param vehicle the vehicle to set	 */	public void setVehicles(ArrayList<VehicleChangeInfo> vehicle) {		this.vehicles = vehicle;	}	/**	 * @return the places	 */	public ArrayList<Places> getPlaces() {		return places;	}	/**	 * @param places the places to set	 */	public void setPlaces(ArrayList<Places> places) {		this.places = places;	}	/**	 * @return the speed	 */	public ArrayList<SpeedVehicle> getSpeed() {		return speed;	}	/**	 * @param speed the speed to set	 */	public void setSpeed(ArrayList<SpeedVehicle> speed) {		this.speed = speed;	}}